import React, { Component } from 'react'
import Avatar from './Avatar' 
import Description from './Description'

export default class NotifUnreadCard extends Component {
  constructor(props){
    super(props);

    this.state = {className: "notif_card unread"}

  }

  render() {
    return (
      <div className={props.className} onClick={(this.props.updateClassName(this.state.className))}>
        <Avatar url={props.url}/>
        <Description name={props.name} reaction={props.reaction} message = {props.message} time={props.time} link={props.link}/>
      </div>
    )
  }
}
