import Header from './Header';
import Main from './Main';
import React, { Component } from 'react'


export default class App extends Component {

  constructor(props){
    super(props);
    this.state={counter:0, markAll:false}

    this.updateCounter = this.updateCounter.bind(this)
    this.updateMarkAll = this.updateMarkAll.bind(this)
  }

  updateCounter = (value) =>{
    this.setState({counter: value}) 
  }

  updateMarkAll = ()=>{
    this.state.markAll=true
  }

  render() {
    return (
      <div className="container">
        <Header counter={this.state.counter} updateMarkAll={this.updateMarkAll}/>
        <Main updateCounter={this.updateCounter}/>       
    </div>
    )
  }
}
